def is_already_running():
    if PID_FILE.exists():
        try:
            with open(PID_FILE, "r") as f:
                pid = int(f.read().strip())
            # Check if process exists
            if os.name != 'nt':
                os.kill(pid, 0)
            else:
                # Windows check
                subprocess.run(["tasklist", "/FI", f"PID eq {pid}"], capture_output=True, check=True)
            return True
        except:
            PID_FILE.unlink(missing_ok=True)
    return False

