def _astgrep_fix(
    pattern: str,
    replacement: str,
    path: Path,
    language: str,
    dry_run: bool
) -> int:
    """Apply fix using ast-grep."""
    cmd = [
        ASTGREP_BIN,
        '--pattern', pattern,
        '--rewrite', replacement,
    ]

    if not dry_run:
        cmd.append('--update-all')

    cmd.extend(['--lang', language, str(path)])

    try:
        proc = subprocess.run(cmd, capture_output=True, text=True)
        # Count matches
        return proc.stdout.count('\n')
    except Exception:
        return 0

