def run_rules(
    rules: List[PatternRule],
    path: Path
) -> List[PatternMatch]:
    """Run multiple pattern rules."""
    all_matches = []

    for rule in rules:
        matches = search_pattern(rule.pattern, path, rule.language)
        for match in matches:
            match.pattern = f"{rule.id}: {rule.message}"
        all_matches.extend(matches)

    return all_matches

