def path_to_module_name(path: Path, root: Path) -> str:
    """Convert a file path to a module name."""
    try:
        relative = path.relative_to(root)
        parts = list(relative.with_suffix('').parts)
        return '.'.join(parts)
    except ValueError:
        return path.stem

