def _version_ge(self, version: str) -> bool:
    """Check if target version is >= specified version."""
    target_parts = self.target.split('.')
    version_parts = version.replace('+', '').split('.')

    try:
        for t, v in zip(target_parts, version_parts):
            if int(t) > int(v):
                return True
            elif int(t) < int(v):
                return False
        return True
    except ValueError:
        return False

